mapper.select:
SELECT od.id AS orderId, od.created_time AS createTime, od.pay_method AS payMethod, od.real_pay_amount AS realPayAmount, od.post_amount AS posAmount, os.order_status AS orderStatus FROM orders od LEFT JOIN order_status os ON od.id = os.order_id WHERE od.is_delete = 0 AND od.user_id = #{userId}
AND os.order_status = #{orderStatus}
ORDER BY od.updated_time ASC

SELECT od.id AS orderId, od.created_time AS createTime, od.pay_method AS payMethod, od.real_pay_amount AS realPayAmount, od.post_amount AS posAmount, os.order_status AS orderStatus, oi.id AS itemId, oi.item_name AS itemName, oi.item_img AS itemImg, oi.item_spec_id AS itemSpecId, oi.item_spec_name AS itemSpecName, oi.buy_counts AS buyCounts, oi.price AS price FROM orders od LEFT JOIN order_status os ON od.id = os.order_id LEFT JOIN order_items oi ON od.id = oi.order_id WHERE od.is_delete = 0 AND od.user_id = #{userId}
AND os.order_status = #{orderStatus}
ORDER BY od.updated_time ASC
